const char JS_SCRIPT[] = R"raw(var startRec=document.getElementById('start-rec');var stopRec=document.getElementById('stop-rec');var navbar=document.getElementById('navbar');var navbarToggle=document.getElementById('navbar-toggle');stopRec.style.display=g_isRecording?'list-item':'none';startRec.style.display=g_isRecording?'none':'list-item';startRec.onclick=function(a){a.preventDefault();var b=prompt('Enter session name',g_sessionName);if(b!==null){var c=startRec.querySelector('a');window.location.href=c.href+'?sessionName='+b;}};navbarToggle.onclick=function(a){a.preventDefault();navbar.style.display=navbar.style.display==='block'?'none':'block';};var seGpsMode=document.getElementById('se_gpsMode');var seLogFormat=document.getElementById('se_logFormat');seGpsMode.onchange=function(a){console.log(seGpsMode.value);if(seGpsMode.value=='0')seLogFormat.classList.remove('disabled');else if(seGpsMode.value=='1')seLogFormat.classList.add('disabled');};)raw";